#include<iostream>
#include<vector>

int MIN(std::vector<int>A, int s, int e, std::vector<int> &po) {
    int min = 999999;
    if( e >= A.size() ) {
      e = A.size() - 1;
    }
    std::cout << " s = " << s << " e= " << e << std::endl;
    for( int i = s; i <= e;i++ ){
        if( min > A[i] ) {
           min = A[i];
           po[s-1] = i;
        }
        //min = min < A[i] ? min : A[i];
    }
    return min;
}
void printVector( std::vector<int> &A ) {
  std::cout << std::endl;
  for( auto a : A ) {
    std::cout << " " << a;
  }
  std::cout << std::endl;
}

int jump(std::vector<int> &A) {
    std::vector<int> mi(A.size(), 0);
    std::vector<int> po(A.size(), 0);
    mi[A.size()-1 ] = 0;
    for( int i = A.size() - 2; i >=0; i-- ) {
        if( (A[i] + i ) >= A.size()-1 ) {
            mi[i] = 1;
        } else {
           mi[i] = 1 + MIN(mi, i+1, i+A[i], po );
        }
    }
    printVector(mi);
    printVector(po);
    return mi[0];
}


